mapper.insert:
INSERT INTO
    TRADE_HIST(EMAIL, SN, TRADE_DT, EXCHNG_CD, TOKEN_NAME, NOW_AMT, RGSTRN_DT)
VALUES(
    #{email}
   ,#{sn}
   ,#{tradeDt}
   ,#{exchngCd}
   ,#{tokenName}
   ,#{nowAmt}
   ,#{rgstrnDt}
)

mapper.select:
SELECT NVL(MAX(SN), 0) AS SN
  FROM TRADE_HIST
 WHERE TO_CHAR(RGSTRN_DT, 'YYYYMMDD') = #{rgstrnDt}
   AND EXCHNG_CD = #{exchngCd}
   AND EMAIL = #{email}
